mapper.select:


where t.rec_status='1'

	 and t.customer_id=#{customer_id}


	 and t.live_status=#{live_status}


	 and t.preferred_brand like concat('%',#{preferred_brand},'%')


	 and t.customer_evaluate=#{customer_evaluate}


	 and t.actual_delivery_time like concat(#{actual_delivery_time},'%')


	 and t.contract_delivery_time like concat(#{contract_delivery_time},'%')
